import React, {useState} from "react";
import "./styles.css";
import Collapsible from "./Component/Collapsible";
import SliderComponent from "./Component/Slider";
import TerrainSelector from "./Component/TerrainSelector";
import Slider from "react-input-slider";
import useCollapse from "react-collapsed";

class App extends React.Component {

  render() {
    const [state, setState] = useState({x: 1});
    const { getCollapseProps, getToggleProps, isExpanded } = useCollapse();


    return (
        <div className="App">
          <h1>Code Sandbox</h1>
          <div className="collapsible">
            <div className="header" {...getToggleProps()}>
              {isExpanded ? 'Collapse' : 'Expand'}
            </div>
            <div {...getCollapseProps()}>

              <div className="content">
                <div className="horizontal-slider">

                  <Slider
                      axis="x"
                      x={state.x}
                      onChange={({ x }) => setState(state => ({ ...state, x }))}
                      xmin={1}
                      xmax={1000}
                  />
                  {(state.x != 1) ? <h3>{state.x} sq. miles</h3> : <h3>{state.x} sq. mile</h3>}

                </div>

                <select>
                  <option value={15}>Flat</option>
                  <option value={8}>Hilly</option>
                  <option selected value={3}>Forest</option>
                  <option value={0.3}>Mountains</option>
                </select>
              </div>
            </div>
          </div>
        </div>
    );
  }
}
export default App;
